import { usePostMetadata } from "../utils/getPostMetadata";
import { HomepageHeader } from "./HomepageHeader";
import PostPreview from "./PostPreview";
import { Grid, Box } from "@mui/material";

const HomePage = () => {
  const postMetadata = usePostMetadata();
  const postPreviews = postMetadata.map((post) => (
    <PostPreview key={post.slug} {...post} />
  ));

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        mx: "150px",
        alignItems: "center",
      }}
    >
      <HomepageHeader />
      <Grid
        container
        spacing={2}
        sx={{
          justifyContent: "center",
          width: { xs: "350px", md: "auto" },
        }}
      >
        {postPreviews.map((post, index) => (
          <Grid
            item
            xs={12}
            md={3}
            key={index} // Updated key to use index
            sx={{
              display: "flex", // Added for inner content alignment
              flexDirection: "column", // Stack children vertically
              justifyContent: "space-between", // Distribute space evenly
              height: "300px", // Fixed height
              width: "100%", // Width to fill the container
              border: "1px solid red",
              borderRadius: "15px",
              padding: "10px", // Add some padding inside the item
              //   boxSizing: 'border-box', // Include padding in the width and height
            }}
          >
            {post}
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default HomePage;
