import React, { useState, useEffect } from "react";
import { Typography, Box, Container } from "@mui/material";
import ReactMarkdown from "react-markdown";
import { fetchPostContent } from "../utils/fetchPostContent";
import matter from "gray-matter";

const PostPage = () => {
  const [content, setContent] = useState("");
  const [postContent, setPostContent] = useState("");
  const slug = "post1";

  useEffect(() => {
    fetchPostContent(slug).then((content) => {
      const matterResult = matter(content);
      const headerObject = {
        title: matterResult.data.title,
        date: matterResult.data.date,
        subtitle: matterResult.data.subtitle,
        content: content,
      };

      setPostContent(JSON.stringify(headerObject));
    });
  }, [slug]);

  console.log("postContent", postContent);

  return (
    <Container>
      <Box my={4} textAlign="center">
        <Typography variant="h2" color="textPrimary">
          {/* Title will be here. Parsing the title from markdown might require additional logic */}
        </Typography>
        <Typography variant="subtitle1" color="textSecondary">
          {/* Date will be here. Similarly, parsing the date will need additional logic */}
        </Typography>
      </Box>

      <Box>
        <ReactMarkdown children={postContent} />
      </Box>
    </Container>
  );
};

export default PostPage;
