import { usePostMetadata } from "../utils/getPostMetadata";
import PostPreview from "./PostPreview";
import { Grid, Box } from "@mui/material";

const HomePage = () => {
  const postMetadata = usePostMetadata();
  const postPreviews = postMetadata.map((post) => (
    <PostPreview key={post.slug} {...post} />
  ));

  const Header = (
    <header>
      <div className="text-center bg-slate-800 p-8 my-6 rounded-md">
        <image
          src="/logo192.png"
          width={40}
          height={40}
          className="mx-auto"
          alt={"logo"}
        />
        <a href="/">
          <h1 className="text-2xl text-white font-bold mt-4">My First Blog</h1>
        </a>
        <p className="text-slate-300">Portfolio and Technology</p>
      </div>
    </header>
  );

  return (
    <Box
      sx={{
        mx: "150px",
      }}
    >
      <Grid container spacing={2}>
        {postPreviews.map((post) => (
          <Grid item xs={12} md={6} key={post.key}>
            {post}
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default HomePage;
